```tsx
import { DefaultTheme } from 'styled-components';

export const ligthTheme: DefaulTheme = {
  bgColor: '#111',
  textColor: 'teal',
  btnColor: 'tomato'
};
export const darkTheme: DefaulTheme = {
  bgColor: '#000',
  textColor: 'whitesmoke',
  btnColor: 'teal'
};
```

